const addToCart = (id) =>{
let cart;
const quantity = cart[id]
let storedItem = localStorage.getItem('cart-item');
if(storedItem){
    cart = JSON.parse(storedItem);
}
else{
    cart = {};
    cart.id = 1;
}
localStorage.setItem('cart-item',JSON.stringify(cart));
}
export {addToCart};
