const addToCart = (id) =>{
let cart;
let storedItem = localStorage.getItem('cart-item');
if(storedItem){
    cart = JSON.parse(storedItem);
}
else{
    cart = {};
}
let quantity = cart[id];
if(quantity){
    quantity = cart[id] + 1;
}
else{
    cart[id] = 1;
}
localStorage.setItem('cart-item',JSON.stringify(cart));
}
export {addToCart};
