import './App.css';
function App() {
  return (
    <div className="App">
    <Battery name='Samsung Galaxy'></Battery>
    <Battery name='iPhone'></Battery>
    <Battery></Battery>
    </div>
  );
}
function Battery(props){
  return(
    <div>
      <h1>Phone Type: {props.name}</h1>
      <h3>Charge Amount:</h3>
      <button>Boost Charge</button>
      </div>
  )
}
export default App;
