const addToCart = (id) =>{
let cart;
const store = localStorage.getItem('store-item');
if(store){
    cart = JSON.parse(store);
}
else{
    cart = {};
}
const quantity = cart[id];
if(quantity){
    const newCart = quantity + 1;
    cart[id] = newCart;
}
else{
    cart[id] = 1;
}
localStorage.setItem('store-item', cart);
}
export {addToCart};
