import React from 'react';
import { addToCart, objRed, reducer, removeFromCart } from '../../utilities/fakedb';
import './User.css';
const User = (props) => {
    const {name, age, phone, address, email, company, gender, balance, id} =props.user;
    const addCart = () =>{
        console.log('Add To Cart: ', id, name);
    }
    const addCart2 = (id) =>{
        console.log('Add To Cart2: ',id, name);
    }
    const showCart2 = () =>{
        addCart2(id);
    }
    const local = (id) =>{
        addToCart(id);
    }
    const remove = id =>{
        removeFromCart(id);
    }
    const products = [
        {id: 1, name: "Watch", price: 500},
        {id: 2, name: "Mug", price: 250},
        {id: 3, name: "Diary", price: 300}
    ];
    const result = reducer();
    console.log(result);
    const result2 = objRed(products);
    console.log(result2);
    return (<>
    <h1>Array Total: {result}</h1>
    <h1>Object Total: {result2}</h1>
    <div className='user'>
            <h1>ID: {id}</h1>
            <h2>Name: {name}</h2>
            <h3>Gender: {gender}</h3>
            <h3>Age: {age}</h3>
            <h3>Phone: {phone}</h3>
            <h3>Email: {email}</h3>
            <h3>Company: {company}</h3>
            <h3>Balance: {balance}</h3>
            <h3>Address: {address}</h3>
            <button onClick={addCart}>Add To Cart: 1</button>
            <button onClick={showCart2}>Add To Cart: 2</button>
            <button onClick={() =>{addCart2(id)}}>Add To Cart: 3</button>
            <button onClick={()=>local(id)}>Local Storage: Multiple</button>
            <button onClick={()=>remove(id)}>Delete</button>
        </div>
    </>
    );
};

export default User;
